import { useState, useEffect } from "react";
import "./App.css";
import { getData, postForm} from "./FormAPI";
import FormInput from "./components/FormInput";
import axios from 'axios';
import Selection from "./components/Selection";

const App = () => {
  const [values, setValues] = useState({
    username: "",
    email: "",
    password: "",
    confirmPassword: "",
    occupation: "",
    state: "",
  });
  const initialValue = {name: "", email: "", password: "", occupation: "", state: ""};
  const [formValues, setFormValue] = useState(initialValue);
  const [occupations, setOccupcations] = useState();
  const [states, setStates] = useState();
  const [chooseOccupations, setChooseOccupations] = useState();
  const [chooseStates, setChooseState] = useState();


  const inputs = [
    {
      id: 1,
      name: "username",
      type: "text",
      placeholder: "Username",
      errorMessage:
        "Username should be 3-16 characters and shouldn't include any special character!",
      label: "Username",
      pattern: "^[A-Za-z0-9]{3,16}$",
      required: true,
    },
    {
      id: 2,
      name: "email",
      type: "email",
      placeholder: "Email",
      errorMessage: "It should be a valid email address!",
      label: "Email",
      required: true,
    },
    {
      id: 3,
      name: "password",
      type: "password",
      placeholder: "Password",
      errorMessage:
        "Password should be 8-20 characters and include at least 1 letter, 1 number and 1 special character!",
      label: "Password",
      pattern: `^(?=.*[0-9])(?=.*[a-zA-Z])(?=.*[!@#$%^&*])[a-zA-Z0-9!@#$%^&*]{8,20}$`,
      required: true,
    },
  ];

  const selectInputs = [
    {
      id: 4,
      name: "occupation",
      occupations: "",
      errorMessage:
        "Please choose your occupation",
      label: "Occupation",
      required: true,
    },
    {
      id: 5,
      name: "state",
      errorMessage:
        "Please choose your state",
      label: "State",
      required: true,
    }


  ]


  const handleSubmit = (e) => {
    e.preventDefault();
  }

  const onChange = (e) => {
    setValues({ ...values, [e.target.name]: e.target.value });
  };

  useEffect(() => {
    getData()
      .then((data) => {
        setStates(data.states)
        console.log(data.states)
        setOccupcations(data.occupations)
        console.log(data.occupations)
      })
  }, [])


  return (
    <div className="app">
      <div className="form">
      <h1>Register</h1>
        <form onSubmit={handleSubmit}>
          {inputs.map((input) => (
            <FormInput
              key={input.id}
              {...input}
              value={values[input.name]}
              onChange={onChange}
            />
          ))}

          {selectInputs.map((selectInput) => (
            <select 
              className="select"
              name={selectInput.name} 
              label={selectInput.label}>
              {occupations && occupations.map((occupation) => (
                <option 
                  value={occupation} key={occupation}>
                  {occupation}
                </option>
              ))}
            </select>
          ))}
          <button>Submit</button>
        </form>
      </div>

    </div>
  );
};

export default App;
