import { useState, useEffect } from "react";
import "./App.css";
import { getOccupcations, getStates, postForm} from "./FormAPI";
import FormInput from "./components/FormInput";
import Selection from "./components/Selection";
import axios from 'axios';

const App = () => {
  const [values, setValues] = useState({
    username: "",
    email: "",
    password: "",
    confirmPassword: "",
    occupation: "",
    state: "",
  });
  const initialValue = {name: "", email: "", password: "", occupation: "", state: ""};
  const [formValues, setFormValue] = useState(initialValue);
  const [occupations, setOccupcations] = useState();
  const [states, setStates] = useState();
  const [chooseOccupations, setChooseOccupations] = useState();
  const [chooseStates, setChooseState] = useState();


  const inputs = [
    {
      id: 1,
      name: "username",
      type: "text",
      placeholder: "Username",
      errorMessage:
        "Username should be 3-16 characters and shouldn't include any special character!",
      label: "Username",
      pattern: "^[A-Za-z0-9]{3,16}$",
      required: true,
    },
    {
      id: 2,
      name: "email",
      type: "email",
      placeholder: "Email",
      errorMessage: "It should be a valid email address!",
      label: "Email",
      required: true,
    },
    {
      id: 3,
      name: "password",
      type: "password",
      placeholder: "Password",
      errorMessage:
        "Password should be 8-20 characters and include at least 1 letter, 1 number and 1 special character!",
      label: "Password",
      pattern: `^(?=.*[0-9])(?=.*[a-zA-Z])(?=.*[!@#$%^&*])[a-zA-Z0-9!@#$%^&*]{8,20}$`,
      required: true,
    },
  ];

  const options = ["Head of Shrubbery","Interim Substitute Teacher","Water Softener","Listener of the House","Really Good Dancer","Gainfully Unemployed","Alexa Impersonator","Chard Farmer","Chief Frolicker (Jolly)","Entry-level Seat Recliner","CEO (Summer Internship)","Widget Fabricator","Underwater Basket Weaver"]

  const handleSubmit = (e) => {
    e.preventDefault();
    getStates()
    console.log(getStates())
  };

  const submitFeedback = (e) => {
    if (e === 201){
        console.log('submit successful')
        alert("submit successful")
    }
    else{
        console.log('error')
        alert("error")
    }
}

  const onChange = (e) => {
    setValues({ ...values, [e.target.name]: e.target.value });
  };

  const handleOccupcations = (e) => {
    console.log(e.target.value, 'handleOccupcations')
    setChooseOccupations(e.target.value)
    const { name, value } = e.target;
    console.log(name, value)
    setFormValue({...formValues, [name]: value});
    console.log(formValues)
}

useEffect(() => {
  fetch('https://frontend-take-home.fetchrewards.com/form')
      .then(res => {
          return res.json()
      })
      .then((data) => {
          setStates(data.states)
          setOccupcations(data.occupations)
      })
  }, []);

  return (
    <div className="app">
      <form onSubmit={handleSubmit}>
        <h1>Register</h1>
        {inputs.map((input) => (
          <FormInput
            key={input.id}
            {...input}
            value={values[input.name]}
            onChange={onChange}
          />
        ))}
        <label className='formInput'>Occupation</label>
        <select className="formInput" 
          name="occupation" 
          value={ formValues.occupation } 
          onChange={handleOccupcations}
          placeholder="Please Choose">
          {occupations && occupations.map((occupation) => (
          <option value={occupation} key={occupation}>
            {occupation}
          </option>
          ))}
        </select>







        <button>Submit</button>
      </form>
    </div>
  );
};

export default App;
